import React, { Component }  from 'react';
import { TouchableOpacity, View} from 'react-native';
import { Container, Header, Content, Form, Item, Input, Label, DatePicker, Button, Text, Icon } from 'native-base';

export default class Pengajuan extends Component {
  constructor(props) {
    super(props);
    var date = new Date().getDate();
    var month = new Date().getMonth() + 1;
    var year = new Date().getFullYear();

    this.state = { chosenDate: new Date() };
    this.setDate = this.setDate.bind(this);
    this.current_date = date;
    this.current_month = month;
    this.current_year = year;
  }
  setDate(newDate) {
    this.setState({ chosenDate: newDate });
  }
  getCurrentDate=()=>{

      var date = new Date().getDate();
      var month = new Date().getMonth() + 1;
      var year = new Date().getFullYear();

      //Alert.alert(date + '-' + month + '-' + year);
      // You can turn it in to your desired format
      return date + '-' + month + '-' + year;//format: dd-mm-yyyy;
  }

  render() {
    return (
      <Container>
        <Header />
        <Content>
          <Form>
            <Item stackedLabel>
              <Label>Nomor Transaksi</Label>
              <Input />
            </Item> 
            <Item stackedLabel>
              <Label>
                Tanggal Transaksi
              </Label>
              <Label>
                {this.state.chosenDate.toString().substr(4, 12)}
              </Label>
            </Item> 
            <Item stackedLabel>
              <Label>Nomor Nasabah</Label>
              <Input />
            </Item>
            <Item disabled stackedLabel>
              <Label>Tanggal Peminjaman</Label>
              <DatePicker
                defaultDate={new Date(2025, 12, 31)}
                minimumDate={new Date(2025, 12, 31)}
                maximumDate={new Date(2025, 12, 31)}
                locale={"en"}
                timeZoneOffsetInMinutes={undefined}
                modalTransparent={false}
                animationType={"fade"}
                androidMode={"default"}
                placeHolderText={this.state.chosenDate.toString().substr(4, 12)}
                textStyle={{ color: "green" }}
                placeHolderTextStyle={{ color: "#d3d3d3" }}
                onDateChange={this.setDate}
                disabled={false}
                />
            </Item>
            <Item stackedLabel>
              <Label>Keterangan</Label>
              <Input />
            </Item>
            <Item stackedLabel last>
              <Label>Status</Label>
              <Input />
            </Item>
          </Form>
        </Content>
        <Content>
        <Button iconLeft>
            <Icon name='home' />
            <Text>Home</Text>
          </Button>
          <Button iconLeft transparent primary>
            <Icon name='beer' />
            <Text>Pub</Text>
          </Button>
        </Content>
      </Container>
    );
  }
}



